#!/usr/bin/python
"""
Module parses HTTP and HTTPS links from any given string and returns a list of links
Syntax: link_parser.parse_body(sting_of_data_containing_links or variable_assigned_to_string_of_data_containing_links) 
"""
import re
#import time
email_body = ""
linksList = []

def parser(input_body):
  
  linksList = re.compile(r'http.?://[a-z,/,\.,\d,\?,=,\-,\+,#,_,&,;,\,,:,@,%,]*', re.IGNORECASE).findall(input_body)
  linksList = list(set(linksList))			# To get unique links only
  return linksList
